15 // Update the Begin and End stationing values for the Route with the ID.   workspace . PerformOperation ( true ,   esriMultiuserEditSessionMode . esriMESMVersioned , 
    ()= 
    { 
        var   route   =   workspace . GetFeatureClass ( typeof ( RouteCenterline )); 
        var   filter   =   new   QueryFilter (); 
        filter . Where   =   ROUTEID = 10423 ; 

         var   centerline   =   route . Map RouteCenterline ( filter ). SingleOrDefault (); 
         centerline . Begin   =   0 ; 
         centerline . End   =   100 ; 
         centerline . Update (); 

         return   true ;   // Commit the operation. 
     });
